record.field:
// get the datasource
let data_source = this.get_data_source()
if (!data_source) {
  return;
}

// get the catorgories
let categories = data_source.get_categories()
if (categories.length == 0) {
  return;
}

// get the measures
let measures = data_source.get_measures()
if (data_source.fields_as_category()) {
  let series = []
  let records = data_source.get_records()
  for (var i=0; i < records.length; i++) {
    let record = records[i]
    let series_data = []
    for (let j = 0; j < measures.length; j++) {
      series_data.push(record[measures[j]])
    }
    series.push({
      data: series_data,
      type: 'bar',
      showBackground: true,
      backgroundStyle: {
        color: 'rgba(180, 180, 180, 0.2)'
      }
    })
  }
  // generate the option, you can change the title, legend, tooltip, etc.
  let option = {
    xAxis: {
      type: 'category',
      data: categories
    },
    yAxis: {
      type: 'value'
    },
    series: series
  };

  this.setOption(option)
} else {
  let series = []
  for (let i = 0; i < measures.length; i++) {
    let measure = measures[i]
    let raw_field = data_source.get_raw_field(measures[i])
    series.push({
        data: data_source.get_col_values(measure),
        type: raw_field && raw_field.series_type ? raw_field.series_type : 'bar',
        showBackground: true,
        name: measure,
        backgroundStyle: {
          color: 'rgba(180, 180, 180, 0.2)'
        }
    })
  }
  // generate the option, you can change the title, legend, tooltip, etc.
  let option = {
    xAxis: {
      type: 'category',
      data: data_source.get_col_values(categories[0], (val) => {

          // check field type
          if (val instanceof Array) {
              if (val.length == 2) {
                  return {
                    value: val[1],
                    origin_value: val
                  }
              }
          } 

          // check field is selction
          if (data_source.is_selection(categories[0])) {
            return {
              value: data_source.get_selection_val(categories[0], val),
              origin_value: val,
            }
          }

          return val
      })
    },
    yAxis: {
      type: 'value'
    },
    series: series
  };

  this.setOption(option)
}


let option = {
  xAxis: {
    type: 'category',
    data: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun']
  },
  yAxis: {
    type: 'value'
  },
  series: [
    {
      data: [120, 200, 150, 80, 70, 110, 130],
      type: 'bar',
      showBackground: true,
      backgroundStyle: {
        color: 'rgba(180, 180, 180, 0.2)'
      }
    }
  ]
};
this.setOption(option)
