record.field:
let data_sources = this.get_data_sources()
if (!data_sources) {
  return
}

if (data_sources.length <= 1) {
  return
}

let data_source1 = data_sources[0]
let data_source2 = data_sources[1]

// get aggregate values
let aggregate_value1 = data_source1.get_first_aggregate_column_value()
let aggregate_value2 = data_source2.get_first_aggregate_column_value()

// calculate percent
let percent_value = aggregate_value2 / aggregate_value1 * 100

// set option
let option =  {
  series: [
    {
      type: 'gauge',
      progress: {
        show: true,
        width: 18
      },
      axisLine: {
        lineStyle: {
          width: 18
        }
      },
      axisTick: {
        show: false
      },
      splitLine: {
        length: 15,
        lineStyle: {
          width: 2,
          color: '#999'
        }
      },
      axisLabel: {
        distance: 25,
        color: '#999',
        fontSize: 20
      },
      anchor: {
        show: true,
        showAbove: true,
        size: 25,
        itemStyle: {
          borderWidth: 10
        }
      },
      title: {
        show: false
      },
      detail: {
        valueAnimation: true,
        fontSize: 40,
        offsetCenter: [0, '70%']
      },
      data: [
        {
          value: percent_value.toFixed(2)
        }
      ]
    }
  ]
}
this.setOption(option)

let option = {
  tooltip: {
    formatter: '{a} <br/>{b} : {c}%'
  },
  series: [
    {
      name: 'Pressure',
      type: 'gauge',
      detail: {
        formatter: '{value}'
      },
      data: [
        {
          value: 50,
          name: 'SCORE'
        }
      ]
    }
  ]
};
this.set_option(option);
